int main()
{
    char a,b,c;
    while(scanf("%c%c%c%*c",&a,&b,&c)!=EOF)
    {
        char tmp;
        if(b>a)
        {tmp=b;b=a;a=tmp;}

        if(c>a)
        {tmp=c;c=a;a=tmp;}

        if(c>b)
        {tmp=b;b=c;c=tmp;}

        printf("%c %c %c\n",c,b,a);
